package ru.dvfu.mrcpk.ex13io;import java.io.*;import java.util.ArrayList;import java.util.List;public class Demo01 {    public static void main(String[] args) throws IOException {        FileInputStream inputStream = new FileInputStream("c:/test/data1.txt");        FileOutputStream outputStream = new FileOutputStream("c:/test/data2.txt");        BufferedReader bufferedReader = new BufferedReader(new InputStreamReader(inputStream));        while (inputStream.available()>0){            int str = inputStream.read();            System.out.println(str);        }        while (bufferedReader.ready()) {            System.out.println(bufferedReader.readLine());        }        bufferedReader.close();        inputStream.close();        outputStream.close();    }}